DoSetDbChar('utf8');
E_D("DROP TABLE IF EXISTS `ycd_article`;");
E_C("CREATE TABLE `ycd_article` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `title` varchar(50) NOT NULL,
  `art_info` varchar(255) NOT NULL,
  `art_keyword` varchar(200) NOT NULL,
  `art_content` text NOT NULL,
  `art_writer` varchar(20) NOT NULL,
  `art_time` int(10) unsigned NOT NULL DEFAULT '0',
  `type_id` smallint(5) unsigned NOT NULL,
  `art_url` varchar(200) NOT NULL,
  `art_img` varchar(200) NOT NULL,
  `art_userid` smallint(5) unsigned NOT NULL,
  `sort_order` int(10) unsigned NOT NULL,
  `art_click` int(10) unsigned NOT NULL DEFAULT '0',
  `art_set` int(1) unsigned NOT NULL DEFAULT '0',
  `art_attr` tinyint(4) NOT NULL DEFAULT '0',
  PRIMARY KEY (`id`),
  KEY `type_id` (`type_id`)
) ENGINE=MyISAM AUTO_INCREMENT=52 DEFAULT CHARSET=utf8");
E_D("replace into `ycd_article` values('1','如何降低投资风险？','如何降低投资风险？','如何降低投资风险？','<p>&nbsp;</p><div>如何降低投标风险?</div><div>1、投资者成为网站的VIP会员，如果借款帖逾期了或者形成坏账，损失的本金由网站合作商百分之百垫付。非VIP投资者遇到坏账，损失的本金由网站合作商垫付百分之五十，详情可咨询客服中心的在线客服。</div><div>2、依据借款人的上传资料情况、还款记录、借款详情等其他相关情况进行风险对比、判断。</div><div>3、与借款帖的用户进行沟通，通过手机对话和QQ对话对借款者进行了解。</div><div>4、借贷给好友以及信用等级高的客户。</div>','admin','1382068409','367','','','0','0','0','0','0');");
E_D("replace into `ycd_article` values('2','投标所得需要支付哪些费用？','投标所得需要支付哪些费用？','投标所得需要支付哪些费用？','<p>&nbsp;</p><div>在房产贷网站投资需要支付哪些费用?</div><div>1、VIP会员费150元/年(申请成为VIP的会员)。</div><div>2、投资收益利息管理费10% .(投资人所得利息由投资人自行缴纳所得税）</div>','admin','1382068465','367','','','0','0','0','0','0');");
E_D("replace into `ycd_article` values('3','如何进行充值？','如何进行充值？','如何进行充值？','<p>&nbsp;</p><div>如何充值：</div><div>1、选择线上充值，只需开通网上银行，点击首页-我的账户-充值-用户就可以充值了。</div><div>2、选择线下充值，自行选择网站公布的公司银行账号，通过网上银行转账的方式进行打款。到，您也可以银行柜台进行汇款，保留流水单号。</div><div>3、打款业务办理完后，到线下充值页面填写交易编号，交易时间，交易金额等信息，确认提交。</div><div>4、等待网站审核人员对该笔充值进行审核。可联系网站财务</div><div>温馨提醒：</div><div>（1）尽量不要跨行汇款,例如从交通银行向我们的工行帐号汇款,可能要1至2天时间才会到帐,这样会影响你在本网站的资金操作(如还款.等),如因跨行耽搁时间造成操作上的延误。网站不承担责任。</div><div>（2）由于您的真实姓名及汇款地址,我们是无法得知的。所以切记在汇款时,务必在汇款的基础上增加几分钱, 如100.10元。我们根据银行名称及汇款的尾数就可判断汇款是您的,别人无从冒认。这样就可确保我们及时为您充值。</div>','admin','1382068495','367','','','0','0','0','0','0');");
E_D("replace into `ycd_article` values('4','什么是流转标？','什么是流转标？','什么是流转标？','<p>&nbsp;</p><div>流转标</div><div>债权人将手中的债权拆分成小额等份的债权转让给众多投资者，并且承诺到期进行还款的一种理财标种，称为流转标。投资者对于流转标的购买份额最小为100元/份，流转期限由借款人确定，流转的周期不同，回报收益不同；通常流转期限越长，回报收益则越高。</div>','admin','1382068585','352','','','0','0','0','0','0');");
E_D("replace into `ycd_article` values('6','资金提现问题','资金提现问题','资金提现问题','<p>&nbsp;资金提现问题：</p><div>1. 首先您必须通过实名认证才能申请提现；</div><div>2、点击“我的账户”—“提现”，进入提现账户设置界面，正确填写自己用于接收提现款的银行卡卡号及开户行支行名称。银行卡号的开户人姓名必须与网站账户的真实姓名一致，确定提交；</div><div>3、提现账号设置后，您就可以“账户提现”界面输入交易密码和提现金额，点击申请提现；</div><div>4、房产贷工作人员将在后台审核提现申请，并将合格的提现申请提交给房产贷财务部；</div><div>5、财务部将提现申请提交到提现银行卡的发卡行进行处理。一般情况下，发卡行会在收到用户提交提现申请的隔天进行处理，钱款具体到达用户银行卡的时间要视各银行处理速度和当天提现申请排队情况而定。一般提现的处理时间在一天之内，资金将在1~2天内到达客户账上。</div><div>请用户务必于每个工作日的下午16:00点之前提交提现申请，每个工作日16:00之后提交的提现申请在当天将不会得到及时处理。大额资金提现可直接联系网站财务超过2个工作日仍未到账可能产生退票，退票的原因有以下几种情况：</div><div>1、开户行信息有误</div><div>2、银行信息有误</div><div>3、账号或户名有误</div><div>4、账户状态异常</div><div>5、账户不存在</div><div>6、账户不存在或状态异常</div><div>7、账户类型有误</div><div>8、账号有误</div><div>9、账户非法</div><div>10、户名有误</div><div>11、卡号不合法或不存在</div><div>12、账户已销户</div><div>13、此账户未缴纳小额账户管理费</div><div>14、因客户姓名不符</div>','admin','1382068824','352','','','0','0','0','0','0');");
E_D("replace into `ycd_article` values('7','什么是担保借款标？','什么是担保借款标？','什么是担保借款标？','<p>&nbsp;</p><div>担保借款标</div><div>担保借款标显示标记\"快\"，担保标属于给力标的一种，借款到期日当天由网站风险金补偿本金和利息还款，债权转让为房产贷网站所有。担保标逾期后，每天按千分之二收取罚息，本金利息及罚息全部为房产贷网站收取。</div><div>担保标投标规则：发布担保标之后再进行线下充值方可投标（发标之前的线下充值不能投标），账户资金添加成功后投标界面即有个担保标可用投标额度。</div><div>&nbsp;</div><div>注：线下充值用户需填写用户名，充值金额，充值银行以及银行卡号。</div>','admin','1382068867','352','','','0','0','0','0','0');");
E_D("replace into `ycd_article` values('8','什么是秒还借款标？','什么是秒还借款标？','什么是秒还借款标？','<p>&nbsp;</p><div>秒还借款标</div><div>&nbsp; &nbsp;秒还标显示标记“秒”，借款者发布秒还标，利息和管理费将被冻结，投标满后，系统自动审核通过，发标人瞬间送出利息和管理费，投资者则收回本金和利息。秒还标是一种娱乐庆祝送钱的标，把快乐送给大家。</div>','admin','1382068891','352','','','0','0','0','0','0');");
E_D("replace into `ycd_article` values('9','什么是抵押借款标？','什么是抵押借款标？','什么是抵押借款标？','<p>&nbsp;</p><div>抵押借款标</div><div>&nbsp; &nbsp;抵押借款标显示标抵押，是房产贷经过线下严格核查借款人资产负债，抵押担保手续（不仅限）、有关政府以及商业银行推荐、优质资产和股权质押，确保风险控制在合理的范围内。抵押标借款者对象一般为长三角地区优质中小微企业，是房产贷重点发展对象。借款人到期还款出现困难，借款到期日当天由网站风险金补偿本金和利息还款，债权转让为房产贷网站所有。抵押标逾期后，每天按千分之二收取罚息，本金利息及罚息全部为房产贷网站收取.</div>','admin','1382068954','352','','','0','0','0','0','0');");
E_D("replace into `ycd_article` values('10','什么是净值借款标？','什么是净值借款标？','什么是净值借款标？','<p>&nbsp;</p><div>&nbsp;净值借款标</div><div>&nbsp; 净值借款标显示标记“净”，允许发布的最大金额 为净资产，净资产负数 0 、500元以下金额不能发标。 可以循环借款。但用户提现将受到限制。他是黄牛标的初始形态，未来房产贷将择机推出净值借款标手续费，利息等方面进行更新改革。如果客户净资产大于借款金额，房产贷允许发布净值借款标用于临时周转。他是一种相对安全系数很高的借款标，因此利率方面可能比较低，适合资金黄牛，用户可以借助此标放大自己的投资杠杠。净值借款标未补偿本金前借款人还款，本息按正常标准还款，另外逾期每天按千分之二收取罚息，投标人和房产贷各收取千分之一.</div><div>净值借款标补偿本金后借款人还款，本息按正常标准还款，另外逾期每天按千分之二收取罚息，本金利息及罚息全部为房产贷收取.</div><div>净值借款标逾期后第30~33天由网站先行补偿本金还款。</div>','admin','1382069036','352','','','0','0','0','0','0');");
E_D("replace into `ycd_article` values('11','什么是信用借款标？','什么是信用借款标？','什么是信用借款标？','<p>&nbsp;</p><div>&nbsp;信用借款标</div><div>信用借款标显示“信”字标记，是一种免抵押、免担保、纯信用，最高授信为10万的小额个人信用贷款标，主要面向公务员、医生或教师等机关企事业单位的在编人员。如借款人到期还款出现困难，逾期第30~33天由网站风险金补偿，债权转让为房产贷网站所有。</div><div>信用借款标未补偿本金前借款人还款，本息按正常标准还款，另外逾期每天按千分之二收取罚息，投标人和房产贷各收取千分之一.</div><div>信用借款标补偿本金后借款人还款，本息按正常标准还款，另外逾期每天按千分之二收取罚息，本金利息及罚息全部为房产贷收取.</div><div>信用借款标逾期后第30~33天由网站先行补偿本金还款.</div><p>&nbsp;</p>','admin','1382069101','352','','','0','0','0','0','0');");
E_D("replace into `ycd_article` values('13','什么是等额本息还款法？','什么是等额本息还款法？','什么是等额本息还款法？','<p>&nbsp;</p><div>什么是等额本息还款法？</div><div>定义：等额本息还款法是每月以相等的还本付息数额偿还贷款本息，即借款人每月按相等的金额偿还贷款本息，其中每月贷款利息按月初剩余贷款本金计算并逐月结清。</div><div>&nbsp;</div><div>由于每月的还款额相等，因此，在贷款初期每月的还款中，剔除按月结清的利息后，所还的贷款本金就较少；而在贷款后期因贷款本金不断减少、每月的还款额中贷款利息也不断减少，每月所还的贷款本金就较多。&nbsp;</div><div>&nbsp;</div><div>其计算公式如下：</div><div>&nbsp;</div><div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 贷款本金×月利率×（1＋月利率）^ 总还款期数</div><div>月均还款额　＝　---------------------------------------------------------------</div><div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; （1＋月利率）^ 总还款期数－1</div>','admin','1382076099','375','','','0','0','0','0','0');");
E_D("replace into `ycd_article` values('14','借款标的进度达到100%，需要补充哪些资料成为最终审核的条件?','借款标的进度达到100%，需要补充哪些资料成为最终审核的条件?','借款标的进度达到100%，需要补充哪些资料成为最终审核的条件?','<p>&nbsp;</p><div>&nbsp; &nbsp; 借款标的进度达到100%，需要补充哪些资料成为最终审核的条件?</div><div>&nbsp; &nbsp; 个人信用报告：凭本人身份证到人民银行免费打印个人信用报告</div><div>&nbsp; &nbsp; 其它地方借款用途及还款情况（如有必要提前说明）</div><div>&nbsp; &nbsp; 毕业证书及其它证书，行驶证，社保，结婚证明等，如有信用卡必须提供信用卡最新对账单扫描上传，补充家人资料需要得到当事人许可，网站有权征询当事人意见。</div><div>&nbsp; &nbsp; 借款用途必须有相应资料证明，如进货单，报税单，店面的租赁合同，营业执照等。</div><div>&nbsp; &nbsp; 基本资料必须提交审核。其它补充资料，有效证明越多，相应的借款额度也会提高。</div><div>&nbsp; &nbsp; 借款招标时，基本资料必须上传全面，借款在招标过程中可以补充其它资料，借款标必须写清借款用途及还款来源。并及时回答投资者在借款标中的留言。</div><div>&nbsp; &nbsp; 第一次借款，除非网站特别允许，借款金额必须在5千元以内，网站会根据充值还款及后续资料补充情况来重新调整额度。5千元以上的借款必须要提交个人信用报告。</div><p>&nbsp;</p>','admin','1382076165','375','','','0','0','0','0','0');");
E_D("replace into `ycd_article` values('15','贷款上传资料应注意哪些事项？','贷款上传资料应注意哪些事项？','贷款上传资料应注意哪些事项？','<p>&nbsp;</p><div>上传资料应注意的哪些事项？</div><div>&nbsp;</div><div>1、注意把图片大小控制在1024KB内；</div><div>&nbsp;</div><div>2、图片上传时应该注意图片的方向；</div><div>&nbsp;</div><div>3、所有证件应以扫描件的形式上传，以保证图片的清晰度；</div><div>&nbsp;</div><div>4、上传的资料若连续几页，应在备注说明一栏注明页码。如“信用报告第1页” 、“ 手机清单第1页”等；</div><div>&nbsp;</div><div>5、若上传的资料为网上截图的。要注意截图的范围，不要漏截重要信息部分。</div><div>&nbsp;</div><div>6、上传资料应保证图片的内容真实，图片不得涂画。</div>','admin','1382076218','375','','','0','0','0','0','0');");
E_D("replace into `ycd_article` values('16','贷款者费用','贷款者费用','贷款者费用','<p>&nbsp;</p><div>&nbsp;[贷款者]</div><div>1、普通会员上传资料审核，并通过审核后即可申请VIP会员，VIP会员信息费150元/年（申请VIP成功后扣除）。</div><div>2、网站风险补偿资金：按借款期限收取,每个月按借款本金收取0.5%,网站风险补偿资金不计息，不退还，在借款金额中直接扣除。（秒还标不收取管理费；净值标收取金额的0.1%）。</div><div>3、借入者借款逾期时，如主动向网站声明并采取积极措施还款，网站将尽力帮助其解决困难。如果借款者恶意逃避，不接电话或者停机逃避债务，将受到罚息惩罚，逾期欠款每天加收逾期本金千分之八的罚息，逾期三十天以上加收电话催收费用每天千分之四。</div>','admin','1382076274','375','','','0','0','0','0','0');");
E_D("replace into `ycd_article` values('17','填写借款承诺书注意事项','填写借款承诺书注意事项','填写借款承诺书注意事项','<p>&nbsp;</p><div>填写借款承诺书的注意事项</div><div>一、下载网站发布的最新版本借款承诺书(不得修改借款承诺书的内容，否则将上传承诺书作审核拒绝处理)</div><div>二、承诺书的填写规范：用黑色笔正楷填写承诺书空白处的内容，字迹必须清晰。(注：填写内容不得有涂改)</div><div>三、承诺书的规范手印(注：必须使用红色印泥按手印，指纹必须按完整清晰，手印按在本人右下角签名处，且不能重复按印)</div><div>四、上传借款承诺书必须与视频认证承诺书时上传的一致(扫描必须完整清晰)</div>','admin','1382076310','375','','','0','0','0','0','0');");
E_D("replace into `ycd_article` values('18','用户初审的准备资料有哪些？','用户初审的准备资料有哪些？','用户初审的准备资料有哪些？','<p>&nbsp;</p><div>1、借款者必须提供以下资料：</div><div>身份证：要求借款者本人二代身份证，原件正反面扫描上传。</div><div>户口本：要求提供户主页，本人页，家人页，原件扫描上传。</div><div>手机通话清单：提供本人使用的近期三个月的通话纪录详单并完整截图每月各六个页面上</div><div>&nbsp;100%满标复审需补充的资料</div><div>1、工作证明：工薪阶层:真实在职三个月以上工作单位劳动合同证明、社保、提供电话地址验证；企业主：提供店铺租赁合同、营业执照、 &nbsp;国地 税；企业：提供公司营业执照、税务登记证、国地税、组织机构代码、近期六个月的水电费清单、近 &nbsp;六 个月的对公流水（注：加盖公章）</div><div>2、银行流水：最近三个月工资流水，银行流水，银行柜台打印盖章，扫描原件上传。</div><div>3、视频认证：</div><div>4、直系亲属的联系方式</div><div>5、配偶的身份证复印件（已婚者）</div><div>5、生活照（半年内）</div><div>6、借款承诺书（原件）</div><div>7、第三联系人的联系方式</div><div>8、现住址证明</div><div>9、个人信用报告（5000含以上元借款额度）：本人携带身份证前往当地人民银行即可免费办理。</div>','admin','1382076359','375','','','0','0','0','0','0');");
E_D("replace into `ycd_article` values('19','如何获得借款资格？','如何获得借款资格？','如何获得借款资格？','<p>&nbsp;</p><p align=\"left\" style=\"color:rgb(51, 51, 51);font-family:Tahoma, Microsoft Yahei, Simsun;font-size:14px;orphans:2;widows:2;background-color:rgb(252, 251, 251);\">&nbsp;</p><div>如何获得借款资格?</div><div>借款人必须满足的基本条件：</div><div>1、具有完全民事能力及独立经济能力的自然人</div><div>2、月收入3000元以上</div><div>2、工作收入或经营稳定</div><div>3、具有明确的借款用途</div><div>4、愿意向网站提供详细的个人资信证明材料</div><div>哪些人群更容易获得借款资格?</div><div>房产贷是一家盈利性网站，主要为具有独立经济能力的人群提供贷款服务。</div><div>对于固定收入人群，工作、生活关系稳定，收入、支出费用稳定，都能获得基本的借款资格。如：教师、医生、公务员、具有高级技术职称的技术员等。另外，能够提供固定劳动合同的企业、事业单位工作人员也可以获得房产贷的借款资格。</div><div>对于经营收入人群，经营项目产权关系明确，收入、支出、盈利状况稳定，都能获得基本的借款资格。如：个体工商户、有限责任公司股东等。另外，对于学校、医院等非盈利企业主，企业收入也可作为个人信用参考，较易获得借款资格。</div><div>哪些人不适合通过网络借贷平台借款?</div><div>对于固定收入人群，工作、生活关系不稳定，超额负债的借款人不适合通过网络借贷平台借款。如：异地求职，工作时间较短;生活所在地无直系亲属或担保借款人;房贷、车贷的还款金额超过家庭收入50%以上的借款人。</div><div>对于经营收入人群，对经营主体主导权限低;收入、支出记录不能核算清楚;盈利能力下降，经营不善的借款人。如：企业小股东、现金收入高于银行帐户来往、公司亏损状况的借款人。</div><div>如何更快地获得借款资格?</div><div>1、个人信息填写时请完全填写真实资料，并且完整提供资料。</div><div>2、借款人需要第一时间与您的专职客服沟通，配合客服指导上传必须的借款资料。</div><div>3、尽量寻找直系亲属或是第三方担保人共同借款保证。</div>','admin','1382076420','375','','','0','0','0','0','0');");
E_D("replace into `ycd_article` values('20','借款人需要提供哪些借款资料?','借款人需要提供哪些借款资料?','借款人需要提供哪些借款资料?','<p>&nbsp;</p><div>&nbsp; 借款人需要提供哪些借款资料?</div><div>基本资料：身份证、户口本、银行流水、手机清单、住址证明、信用报告、工作合同。</div><div>辅助资料：社会关系资料、工作关系资料、能力证明材料、经营主体资质、经营业绩资料等，房产、车产等资产证明文件。</div><div>由于借款人的个人资信状况不同，所需要提供的辅助资料会有所差异，在会员新注册期间，房产贷为会员提供3个月的新会员辅导服务，您可以随时联络我们的在线客服获得帮助。以下是房产贷网站对各项借款资格的详细要求，请借款人务必详细阅读。</div><div>&nbsp;身份信息资料：身份证、户口本及其它</div><div>身份证：必须是二代身份证正反面原件扫描上传。在进行实名登记时，身份证号码、身份证地址必须与身份证原件上面的信息一致。</div><div>户口本：必须包含封面、内页的完整扫描上传。对于集体户口的会员，要求提供户籍单位的资料。对于不能提供有效户籍证明的会员，其风险评级会提高。</div><div>社会关系资料：住址证明、手机清单、其他联系人、工作关系、结婚证等其它证件</div><div>其他联系人：直系亲属、同事、朋友等至少3名以上联络人姓名、电话。直系亲属身份证明应包含直系亲属二代身份证和户口的扫描件及填写联系电话。若直系亲属为配偶的，还必须提供结婚证，朋友身份证明及联系信息包括朋友的借款信息，可以在其他联系人中保存。若借款人不提供足够的联系人信息，将降低借款额度评级。</div><div>住址证明：要求有个人住址的水电物业单、纸质固话账单、纸质信用卡账单等收件资料的扫描件，且需提供相应的房产证、按揭合同或租赁合同。</div><div>个人地址证明3个月更新1次，若审核有特殊要求时需要即时提供近期更新。</div><div>手机清单：要求具有正常的呼出呼入记录，若通话记录不满足审核要求，您的借款将不被允许。手机通话清单可以去移动营业厅打印后扫描，也可以在网上查询截图。截图时需要包含用户名或手机号，以及未经排序、筛选连贯的通讯记录。若是对手机清单的截图不了解，可以寻求在线客服帮助，或者请客服远程协助帮忙。</div><div>个人手机清单的更新要求每月1次，若审核有特殊要求时需要即时提供近期更新。</div><div>工作关系：应包含个人的劳动合同、名片、工作证、社保详细信息等，会员若不能提供完整的信息和资料，您的工作关系信息将不被采信，将降低借款额度评级。</div><div>征信资料：人行征信报告</div><div>征信报告：个人征信报告需要在当地的人民银行打印，若是对打印信息不了解，可以咨询在线客服。在个人征信报告上传过程中，需要注册原件招描上传。对于一些会员打印资料上传不能被采信的，需要委托房产贷来直接打印的，可以在申请选项中申请打印征信报告。个人征信报告的打印属于收费服务，可以咨询在线客服后确认打印。</div><div>个人征信报告要求6个月更新1次，若审核有特殊要求时需要即时提供近期更新。</div><div>偿还能力信息：银行流水、企业账目、合同订单</div><div>银行流水要求必须有近3个月完整的流水，最迟截止时间为上传时间前3天内。对于某些拥有多张银行卡的会员，要求尽量提供常用银行的流水。若是工薪收入者，银行流水必须包含银行代发工资的记录;若是个体工商户或是企业主，必须提供经营企业的银行流水，以及企业账目。</div><div>银行流水的上传方式可以是银行柜台打印件扫描或是网银截图。柜台打印后需要银行盖公章确认。若是网银截图，要求截取带有用户账户名的页面，同时要求3个月银行流水查询时间的页面以及完整的资金流水页面。若会员对银行流水的截图要求不能够完全把握，可以申请在线客服远程协助，由客服人员协助进行银行流水截图。</div><div>银行流水是对个人收入情况的有效证明，若个人月收入低于3000元/月，我们将不受理您的借款申请。若是企业经营者，在不提供经营流水与企业内部账目的情况下，我们将不受理您的借款申请。</div><div>除银行流水外，企业经营的内部账目、订单、合同、工资单、水电清单等都是能够佐证借款人还款能力的资料。若借款人提供更详细的借款企业或是借款人个人资料，将能获得更高额的个人资信评级。</div><div>除了收入的银行流水，借款人还可以提供一些信用卡账单以参考信用额度及信用使用状况。</div><div>银行流水的更新要求每月1次，若审核有特殊要求时需要即时提供近期更新。</div><div>个人资产证明资料：房产、车产、股权等</div><div>个人资产还包括房产、车产、个人股权及相应企业的资产状况。</div><div>房产：应包含房产证、购房发票、按揭合同、抵押合同、还款账户银行流水，若是自住的房产，应包含水电清单。</div><div>车产：应包含车辆登记证、行驶证、驾驶证，购车发票等。</div><div>企业股权：应包含公司章程、企业资产报表、股东名册等。</div><div>对于一些能够接受个人资产抵押或是质押的会员，房产贷将提供更高额的资信评级。</div><div>个人能力证明资料包含毕业证、学位证书、技能证书、职业证书等。</div><div>综上所述，借款人最起码应提供本人和家属身份证、本人及家属户口本、银行流水、个人征信报告、住址证明、手机清单、工作信息、借款承诺书等资料，同时也必须提供生活信息。若会员希望获得更高资信评级，就应提供更详细的信息。必须的资料是您可能获得借款资格的最低要求，若无其它资料的佐证，在准备好基本资料的情况下仍可能借不到款。</div>','admin','1382076466','375','','','0','0','0','0','0');");
E_D("replace into `ycd_article` values('21','借款人指南','借款人指南','借款人指南','<p>&nbsp;</p><div>&nbsp;</div><div>1．注册账户</div><div>&nbsp;</div><div>&nbsp;填写常用邮箱、用户名和密码，邮箱在以后操作中很重要，用户名取中文和英文都可以，建议您取一个有信任感的用户名，这样能给借出者留下第一好印象，有利于您以后借款进度。密码最好使用数字，字母的组合。（用户名和密码最好写在笔记薄上，以免忘记时方便找回）</div><div>&nbsp;2．填写个人资料信息</div><div>&nbsp;</div><div>&nbsp;登录后，点击我的账户，填写个人资料，按照网站要求填入真实个人资料信息（如居住地，家属电话，工作单位，房产，学历，社保，财务状况等）。如果个人资料弄虚作假填写，一经发现，将永远禁止在本网站借款。</div><div>&nbsp;3．实名认证</div><div>&nbsp;</div><div>&nbsp;在安全中心，点击实名认证。填写真实姓名，身份证号等资料，真实姓名填写后不能更改，提现时银行账号必须和实名认证名字一样，否则提现不能成功。</div><div>&nbsp;4．完善安全中心</div><div>&nbsp;</div><div>&nbsp;点击安全中心，可以修改登录密码、交易密码、设置密码保护问题。</div><div>&nbsp;5．上传证明资料和申请VIP会员</div><div>&nbsp;</div><div>&nbsp;提供第二代身份证正反面，家属身份证正反面，户口本，个人信用报告(本人到中国人民银行或分行免费打印)，房产证或者房屋租赁合同，社保，近3个月银行流水，近3个月手机通话详单，工作证，毕业证，劳动合同，收入证明，生活照片等资料，以上资料必须是扫描上传或者有效截图上传。</div><div>&nbsp;在安全中心，点击VIP信息，选择跟踪客服，VIP会员150元/年（可在借款成功后扣除）。</div><div>&nbsp;</div><div>&nbsp;6.视频认证和手机认证</div><div>&nbsp;</div><div>&nbsp;视频认证前，您需要去网站下载打印借款承诺书（不得私自修改承诺书内容,用于与客服视频认证过程中签字），准备好身份证，户口本原件，找一台有视频的电脑，联系网站客服帮你做视频认证。</div><div>&nbsp;手机认证前，请确保您的手机号码必须是实名登记，并且知道手机服务密码（如果您不知道服务密码，可拨打手机服务商人工服务询问）。网站客服会在视频认证过程中打电话给您，并且会登录您的手机网上营业厅，查询您的手机是否实名，以此来确认您的手机是否实名制。</div><div>&nbsp;</div><div>&nbsp;7. 发布借款标</div><div>&nbsp;</div><div>&nbsp;通过以上步骤后，点击我要贷款，您就可以发布借款标。您在发布借款标前，请务必阅读网站收费规则，并到网站工具箱计算您的借款成本。借款标满标后若通过网站复审，您借款就成功了。</div><div>&nbsp;8. 提现</div><div>&nbsp;</div><div>&nbsp;在我的账户，点击账号提现，设置提现银行卡号(提现卡号必须和您实名认证时登记的名字一样) .若填写错误，则不能提现。</div><div>&nbsp;9. 充值和还款</div><div>&nbsp;</div><div>&nbsp;在还款时间前几天，请充值，在我的账户，点击账户充值，选择您的充值方式进行充值。</div><div>&nbsp;在我的账户，点击正在还款的借款，然后点击还款。（本网站采取等额本息还贷方式）。</div><div>&nbsp;</div><div>&nbsp;10. 申请提高借款额度</div><div>&nbsp;</div><div>&nbsp;如果您借款信用积分达到一定程度，可向网站申请提高借款额度。在我的账户，点击额度申请，您就可以申请提高借款额度，网站会客观、公正审核您的额度申请。</div>','admin','1382076504','375','','','0','0','0','0','0');");
E_D("replace into `ycd_article` values('22','监管部门将调研互联网金融网贷平台','监管部门将调研互联网金融网贷平台','监管部门将调研互联网金融网贷平台','<p>近期央行和深圳市政府金融办将分别对深圳一些金融机构展开调研，调研行程中，网贷(P2P)平台公司成为重要一站。据了解，全国已上线的网贷平台超过500家，并处于不断增长之中，无门槛、缺少监管带来风险聚集的现状亟待改变。<br />调研<br />互联网金融机构<br />证券时报记者获悉，近日，深圳市金融办将组织深交所、深圳证监局、深圳银监局、深圳保监局等监管部门派出机构赴平安保险、招商银行、国信证券、红岭创投等公司调研，将就互联网金融在深圳的发展基础、发展策略和行动路径向业内了解情况、收集意见。<br />据了解，央行原定于9月下旬赴深调研的计划因故延迟，这一计划将于近期重启。央行的调研安排中，网贷平台也成为重要一站。<br />网贷资讯平台——网贷之家总经理朱明春对证券时报记者表示，据统计，目前全国网贷平台总数已经超过500家，平均每天都有两家新的网贷平台上线。火爆的互联网金融概念让大量机构和个人加入网贷行业，使得网贷平台出现供大于求的现象，目前大部分网贷平台处于亏损状态，无门槛、缺监管造成的风险在积聚，这一状态亟待改变。<br />今年以来，央行在各个场合的表态让不少网贷平台开始明确自身的定位。一位参与了今年7月份央行座谈会的网贷平台创始人表示，央行高层在座谈会中表示不反对网贷平台发展，希望网贷平台做银行的补充，吸引民间资金，服务小微企业。<br />据了解，对于建立网贷行业征信系统的课题，央行也在积极研究之中。不过到目前为止，网贷行业仍无明文规范。</p>','admin','1382084554','362','','','0','0','0','0','0');");
E_D("replace into `ycd_article` values('23','P2P网贷迎急速发展期','P2P网贷迎急速发展期','P2P网贷迎急速发展期','<p>P2P(peer-to-peer)互联网金融在中国酝酿七年，似乎在近期迎来了绝佳发展时机。个人用户对理财的需求和中小企业对资金的渴望，造就了庞大的市场需求潜力，并冲击着传统金融格局。<br />据中国电子商务研究中心数据显示，截至去年底，全国规模以上P2P贷款服务平台超过200家，估计2012年全年P2P行业贷款规模在500亿-600亿元，比上年增长300%以上，可统计P2P线上业务借款余额将近100亿元，投资人超过5万人。无论从机构数量还是交易金额来看，网贷行业已初具规模。<br />日前，由中申联合控股集团有限公司旗下网络理财融资服务平台——联合贷上线，这也是国内首个采取线上线下差异化管理、“O2O模式”推动资金融合的P2P平台。<br />浙江大学管理学院副教授王小毅认为，P2P网络借贷的快速发展，不仅帮助了数以万计的小微企业获得资金，也为众多的个人和家庭开辟了一条可以获得持续稳定较高回报的投资途径。从这个角度讲，P2P无疑冲破了“金融囚笼”，开创了“平民金融”时代。<br />北京商报记者 崇晓萌</p>','admin','1382084601','338','','','0','0','0','0','0');");
E_D("replace into `ycd_article` values('24','P2P网贷理财产品须警惕风险','P2P网贷理财产品须警惕风险','P2P网贷理财产品须警惕风险','<p>本报讯(记者 杨汛)网络P2P（peer to peer lending）贷款理财产品如今成了百姓的投资新宠，标榜年化收益率12%以上的产品比比皆是。而专家表示，目前网络贷款的发展远超监管范围，投资者应充分注意其中的风险。<br />网络P2P贷款意为以网络公司为中介机构、对接个人投融资需求的金融服务。由于门槛低、效率高，许多无法通过银行获得贷款的普通人借此得到了小额贷款；同时，资金提供方获得了较高收益。一些网贷平台的P2P理财产品甚至号称年化收益率24%以上，投资起点也不高，部分产品投资起点低至50元。<br />业内专家认为，网贷理财产品热销说明借贷双方的资金需求市场很大，而这些需求通过银行渠道难以释放。不过，背后的风险也不小，根据目前传统的监管模式，很难监督网贷公司的操作流程。投资者要警惕网贷公司只是把钱吸进来，而没有真实的投向。<br />中国投资人协会律师团团长邱宝昌表示，投资者需要警惕网贷的非法集资风险。“非法集资有几个突出的特征，包括未经批准、面向不特定的对象募集资金、承诺在一定期限内给予回报、以合法形式掩盖非法集资的性质等等。网贷和非法集资的界限确实非常模糊，而且许多网贷公司看上去没有违规，实际上它怎么处理资金就很难说了，\"保本\"\"安全\"的宣传未必可信。”</p>','admin','1382084631','338','','','0','0','0','0','0');");
E_D("replace into `ycd_article` values('25','P2P行业首发自律公约','P2P行业首发自律公约','P2P行业首发自律公约','<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 晨报讯(记者 姜樊)近几年，本身作为先进模式引进的P2P模式(个人对个人)，却往往被看作是非法集资、卷款跑路的代名词。日前，中国小额信贷联盟在京正式发布《个人对个人(P2P)小额信贷信息咨询服务机构行业自律公约》。</p><p>&nbsp; 　　P2P小额信贷信息咨询服务业务作为近年来从国外引进的小额信贷创新业务，其基本原理是通过信息和互联网技术，为个人与个人之间的小额信用借贷行为提供信息咨询性质的中介服务，从而满足个人的信贷和投资需求。但是，在快速发展的背后，也出现了一些包括涉嫌非法集资、编造虚假债权、交易成本增加等诸多问题。而P2P行业目前面临准入门槛低、法律监管尚处于“真空”状态等问题。<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 据了解，自律公约要求P2P服务机构必须合法合规经营，完善企业治理和内部控制机制，提升风险管理能力，针对信用风险、操作风险、流动性风险、市场风险等制定积极可行的防范措施。同时积极与征信机构合作，把借款还款信息主动纳入统一的征信体系，建立涵盖P2P服务机构借款人信息的征信管理系统平台，通过数据报送和数据查询的双向数据交流，增强P2P服务机构防范信贷风险的能力。</p>','admin','1382084667','338','','','0','0','0','0','0');");
E_D("replace into `ycd_article` values('34','申请借款用户必须先注册，申请VIP','申请借款用户必须先注册，申请VIP','申请借款用户必须先注册，申请VIP','<p>1．注册账户</p><div>&nbsp;</div><div>&nbsp;填写常用邮箱、用户名和密码，邮箱在以后操作中很重要，用户名取中文和英文都可以，建议您取一个有信任感的用户名，这样能给借出者留下第一好印象，有利于您以后借款进度。密码最好使用数字，字母的组合。（用户名和密码最好写在笔记薄上，以免忘记时方便找回）</div><div>&nbsp;2．填写个人资料信息</div><div>&nbsp;</div><div>&nbsp;登录后，点击我的账户，填写个人资料，按照网站要求填入真实个人资料信息（如居住地，家属电话，工作单位，房产，学历，社保，财务状况等）。如果个人资料弄虚作假填写，一经发现，将永远禁止在本网站借款。</div><div>&nbsp;3．实名认证</div><div>&nbsp;</div><div>&nbsp;在安全中心，点击实名认证。填写真实姓名，身份证号等资料，真实姓名填写后不能更改，提现时银行账号必须和实名认证名字一样，否则提现不能成功。</div><div>&nbsp;4．完善安全中心</div><div>&nbsp;</div><div>&nbsp;点击安全中心，可以修改登录密码、交易密码、设置密码保护问题。</div><div>&nbsp;5．上传证明资料和申请VIP会员</div><div>&nbsp;</div><div>&nbsp;提供第二代身份证正反面，家属身份证正反面，户口本，个人信用报告(本人到中国人民银行或分行免费打印)，房产证或者房屋租赁合同，社保，近3个月银行流水，近3个月手机通话详单，工作证，毕业证，劳动合同，收入证明，生活照片等资料，以上资料必须是扫描上传或者有效截图上传。</div><div>&nbsp;在安全中心，点击VIP信息，选择跟踪客服，VIP会员150元/年（可在借款成功后扣除）。</div><div>&nbsp;</div><div>&nbsp;6.视频认证和手机认证</div><div>&nbsp;</div><div>&nbsp;视频认证前，您需要去网站下载打印借款承诺书（不得私自修改承诺书内容,用于与客服视频认证过程中签字），准备好身份证，户口本原件，找一台有视频的电脑，联系网站客服帮你做视频认证。</div><div>&nbsp;手机认证前，请确保您的手机号码必须是实名登记，并且知道手机服务密码（如果您不知道服务密码，可拨打手机服务商人工服务询问）。网站客服会在视频认证过程中打电话给您，并且会登录您的手机网上营业厅，查询您的手机是否实名，以此来确认您的手机是否实名制。</div><div>&nbsp;</div><div>&nbsp;7. 发布借款标</div><div>&nbsp;</div><div>&nbsp;通过以上步骤后，点击我要贷款，您就可以发布借款标。您在发布借款标前，请务必阅读网站收费规则，并到网站工具箱计算您的借款成本。借款标满标后若通过网站复审，您借款就成功了。</div><div>&nbsp;8. 提现</div><div>&nbsp;</div><div>&nbsp;在我的账户，点击账号提现，设置提现银行卡号(提现卡号必须和您实名认证时登记的名字一样) .若填写错误，则不能提现。</div><div>&nbsp;9. 充值和还款</div><div>&nbsp;</div><div>&nbsp;在还款时间前几天，请充值，在我的账户，点击账户充值，选择您的充值方式进行充值。</div><div>&nbsp;在我的账户，点击正在还款的借款，然后点击还款。（本网站采取等额本息还贷方式）。</div><div>&nbsp;</div><div>&nbsp;10. 申请提高借款额度</div><div>&nbsp;</div><div>&nbsp;如果您借款信用积分达到一定程度，可向网站申请提高借款额度。在我的账户，点击额度申请，您就可以申请提高借款额度，网站会客观、公正审核您的额度申请。</div>','admin','1382325469','379','','','0','0','0','0','0');");
E_D("replace into `ycd_article` values('30','国务院再推金融改革 民资进入有望提速','P2P行业首发自律公约','P2P行业首发自律公约','<p>P2P行业首发自律公约</p>','admin','1382200254','338','','','0','0','0','0','0');");
E_D("replace into `ycd_article` values('33','什么是网贷？','什么是网贷？','什么是网贷？','<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 网贷，又称P2P网络借款，是指个人通过网络平台相互借贷。即，由具有资质的网站（第三方公司）作为中介平台，借款人在平台发放借款标，投资者进行竞标向借款人放贷的行为。网贷平台数量近两年在国内迅速增长，迄今比较活跃的有350家左右。</p>','admin','1382325328','379','','','0','0','0','0','0');");
E_D("replace into `ycd_article` values('35','投标所得需要支付哪些费用？ ','投标所得需要支付哪些费用？ ','投标所得需要支付哪些费用？ ','<div>在房产贷网站投资需要支付哪些费用?</div><div>1、提现费用（祥见网站规则）。</div><div>2、投资收益利息管理费10% (投资人所得利息由投资人自行缴纳所得税）。</div>','admin','1382325518','379','','','0','0','0','0','0');");
E_D("replace into `ycd_article` values('36','我的账户','我的账户','我的账户','<p>如何登陆？</p><p>点击我的账户，到图下的登陆界面，输入你已经申请好的用户名和密码，输入验证码，点击登陆。</p><p><img style=\"float: none; margin: 0px\" alt=\"\" src=\"/UF/Uploads/Article/20131021112447.jpg\" /></p><p>&nbsp;</p><p>用户免费注册</p><p>点击如下图的免费注册链接。</p><p><img style=\"float: none; margin: 0px\" alt=\"\" src=\"/UF/Uploads/Article/20131021112928.jpg\" /></p><p>&nbsp;</p><p>到了下图注册界面，填写相关的用户信息。</p><p><img style=\"float: none; margin: 0px\" alt=\"\" src=\"/UF/Uploads/Article/20131021113132.jpg\" /></p>','admin','1382326297','379','','','0','0','0','0','0');");
E_D("replace into `ycd_article` values('37','P2P网贷迎急速发展期','P2P网贷迎急速发展期','P2P网贷迎急速发展期','<p>&nbsp;</p><div>&nbsp; &nbsp; P2P(peer-to-peer)互联网金融在中国酝酿七年，似乎在近期迎来了绝佳发展时机。个人用户对理财的需求和中小企业对资金的渴望，造就了庞大的市场需求潜力，并冲击着传统金融格局。</div><div>&nbsp; &nbsp; 据中国电子商务研究中心数据显示，截至去年底，全国规模以上P2P贷款服务平台超过200家，估计2012年全年P2P行业贷款规模在500亿-600亿元，比上年增长300%以上，可统计P2P线上业务借款余额将近100亿元，投资人超过5万人。无论从机构数量还是交易金额来看，网贷行业已初具规模。</div><div>&nbsp; &nbsp; 日前，由中申联合控股集团有限公司旗下网络理财融资服务平台——联合贷上线，这也是国内首个采取线上线下差异化管理、“O2O模式”推动资金融合的P2P平台。&nbsp;</div><div>&nbsp; &nbsp; 浙江大学管理学院副教授王小毅认为，P2P网络借贷的快速发展，不仅帮助了数以万计的小微企业获得资金，也为众多的个人和家庭开辟了一条可以获得持续稳定较高回报的投资途径。从这个角度讲，P2P无疑冲破了“金融囚笼”，开创了“平民金融”时代。</div><div>&nbsp;</div>','admin','1382346290','362','','','0','0','0','0','0');");
E_D("replace into `ycd_article` values('38','P2P网贷理财产品须警惕风险','P2P网贷理财产品须警惕风险','P2P网贷理财产品须警惕风险','<p>&nbsp;</p><div>本报讯(记者 杨汛)网络P2P（peer to peer lending）贷款理财产品如今成了百姓的投资新宠，标榜年化收益率12%以上的产品比比皆是。而专家表示，目前网络贷款的发展远超监管范围，投资者应充分注意其中的风险。</div><div>网络P2P贷款意为以网络公司为中介机构、对接个人投融资需求的金融服务。由于门槛低、效率高，许多无法通过银行获得贷款的普通人借此得到了小额贷款；同时，资金提供方获得了较高收益。一些网贷平台的P2P理财产品甚至号称年化收益率24%以上，投资起点也不高，部分产品投资起点低至50元。</div><div>业内专家认为，网贷理财产品热销说明借贷双方的资金需求市场很大，而这些需求通过银行渠道难以释放。不过，背后的风险也不小，根据目前传统的监管模式，很难监督网贷公司的操作流程。投资者要警惕网贷公司只是把钱吸进来，而没有真实的投向。</div><div>中国投资人协会律师团团长邱宝昌表示，投资者需要警惕网贷的非法集资风险。“非法集资有几个突出的特征，包括未经批准、面向不特定的对象募集资金、承诺在一定期限内给予回报、以合法形式掩盖非法集资的性质等等。网贷和非法集资的界限确实非常模糊，而且许多网贷公司看上去没有违规，实际上它怎么处理资金就很难说了，\"保本\"\"安全\"的宣传未必可信。”</div>','admin','1382346334','362','','','0','0','0','0','0');");
E_D("replace into `ycd_article` values('39','美国如何监管P2P平台','美国如何监管P2P平台','美国如何监管P2P平台','<p>&nbsp;</p><div>确保消费者有足够保护，是美国金融监管系统的主要目标之一。美国监管者努力采取措施解决借款者和投资者面对的信息劣势，保证他们能够得到足够的信息以做出合适的决定，同时监督商业操作和销售实践以防止欺骗和滥用。&nbsp;</div><div>美国监管体制对于消费者保护主要关注以下三个方面：第一，公平对待所有消费者（主要是投资者）；第二，保护消费者的隐私（主要是借款人）；第三，消费者意识和教育（借款人和投资者双方）。&nbsp;</div><div>美国证券交易委员会（SEC）的注册要求设立了很高的市场参与门槛，有效阻止了那些没有优秀运营模式的新参与者加入。在接受SEC监管之后，P2P（个人网贷）平台每天都要至少一次或者多次向SEC提交报?告。&nbsp;</div><div>这个要求有些时候显得过分，但是，全面的信息披露有很重要的意义。P2P平台需要将每天的贷款列表提交给SEC，保证当有消费者对P2P平台提起法律诉讼的时候，有存档的记录来证明是否存在错误信息误导消费者。这个措施或许可以使消费者在申请贷款时，免于不实信息的误导。&nbsp;</div><div>P2P平台的注册文件和补充材料包含着广泛的信息，例如经营状况、潜在的风险因素、管理团队的构成和薪酬以及公司的财务状况。任何希望成为P2P平台借款人的消费者必须要尝试尽可能多地了解P2P平台，理解P2P借贷内在的风险。消费者有各种途径可以了解到P2P借贷，这有助于他们衡量自身的风险承受度、多样化投资、理智而非盲目地将钱投入到P2P平台或其他贷款当中。&nbsp;</div><div>另外，美国实行按行为监管。&nbsp;</div><div>美国的金融监管机构很少对各种机构的性质进行区分，而是根据各机构的业务采取执法行动。例如，FTC（联邦贸易委员会）对于银行和P2P机构没有明确的管辖权，但是银行或者P2P机构如果采取了法律意义上的“不公平或者欺骗性行为”，FTC就可以采取执法行动。美国的监管机构认为，只要金融机构提供了全面、真实、无偏差的信息披露和风险提示，作为理性人的消费者经衡量后，就可以根据自身情况做出正确的选?择。&nbsp;</div><div>在向SEC登记注册之前，Prosper和Lending Club允许放款人直接购买贷款份额。P2P平台通过Web Bank，以向P2P平台负有还款义务的信用凭证形式，向借款人发放贷款，同时设立相对应的信用凭证。Web Bank再将这些信用证出售给放款人。这个模式有效地将借款人和放款人直接联系起?来。&nbsp;</div><div>在SEC登记注册之后，P2P平台改变了这一模式，以反映出售给放款人的信用凭证属于证券类别，同时遵守相关的证券法律。借款人的贷款仍然是由Web Bank来发放，然后Web Bank会将债权卖给P2P平台，P2P再将这些贷款又以收益权凭证形式卖给放款人。&nbsp;</div><div>通过这种形式，放款人就成了P2P平台的无担保债权人，而非是借款人的债权人，在借款人违约的情况下，放款人只有很有限的途径去追索。&nbsp;</div><div>Web Bank在整个过程中的作用就是分销贷款，这是P2P平台没有相关牌照所不能做的业务。进一步来说，考虑到有大量的法律和监管法规针对消费者贷款发行这一环节，因而委托有牌照的银行来分销贷款，有助于P2P平台符合这些法律法规的要求，也有利于美国的多头监管，从而保护借贷双方，防止欺诈。&nbsp;</div><div>可以看出，由于中美之间金融市场化程度不同和政策环境不同，因此同样是P2P，其实中美之间的差异很大。但国内存在诸多不利因素，诸如金融市场化程度很低，垄断横行，社会征信体系极不完善，政策不明朗等，却反而带来更大的发展潜力。&nbsp;</div><div>因为P2P平台面对的服务群体更多，市场容量更大，且规则尚不完善，有非常巨大的成长空间，而国内P2P也很有可能成为现有金融体系内的服务补充，这对推动整个金融行业市场化有非常深远的意义。&nbsp;</div>','admin','1382346360','362','','','0','0','0','0','0');");
E_D("replace into `ycd_article` values('40','美国如何监管P2P平台','美国如何监管P2P平台','美国如何监管P2P平台','<p>&nbsp;</p><div>确保消费者有足够保护，是美国金融监管系统的主要目标之一。美国监管者努力采取措施解决借款者和投资者面对的信息劣势，保证他们能够得到足够的信息以做出合适的决定，同时监督商业操作和销售实践以防止欺骗和滥用。&nbsp;</div><div>美国监管体制对于消费者保护主要关注以下三个方面：第一，公平对待所有消费者（主要是投资者）；第二，保护消费者的隐私（主要是借款人）；第三，消费者意识和教育（借款人和投资者双方）。&nbsp;</div><div>美国证券交易委员会（SEC）的注册要求设立了很高的市场参与门槛，有效阻止了那些没有优秀运营模式的新参与者加入。在接受SEC监管之后，P2P（个人网贷）平台每天都要至少一次或者多次向SEC提交报?告。&nbsp;</div><div>这个要求有些时候显得过分，但是，全面的信息披露有很重要的意义。P2P平台需要将每天的贷款列表提交给SEC，保证当有消费者对P2P平台提起法律诉讼的时候，有存档的记录来证明是否存在错误信息误导消费者。这个措施或许可以使消费者在申请贷款时，免于不实信息的误导。&nbsp;</div><div>P2P平台的注册文件和补充材料包含着广泛的信息，例如经营状况、潜在的风险因素、管理团队的构成和薪酬以及公司的财务状况。任何希望成为P2P平台借款人的消费者必须要尝试尽可能多地了解P2P平台，理解P2P借贷内在的风险。消费者有各种途径可以了解到P2P借贷，这有助于他们衡量自身的风险承受度、多样化投资、理智而非盲目地将钱投入到P2P平台或其他贷款当中。&nbsp;</div><div>另外，美国实行按行为监管。&nbsp;</div><div>美国的金融监管机构很少对各种机构的性质进行区分，而是根据各机构的业务采取执法行动。例如，FTC（联邦贸易委员会）对于银行和P2P机构没有明确的管辖权，但是银行或者P2P机构如果采取了法律意义上的“不公平或者欺骗性行为”，FTC就可以采取执法行动。美国的监管机构认为，只要金融机构提供了全面、真实、无偏差的信息披露和风险提示，作为理性人的消费者经衡量后，就可以根据自身情况做出正确的选?择。&nbsp;</div><div>在向SEC登记注册之前，Prosper和Lending Club允许放款人直接购买贷款份额。P2P平台通过Web Bank，以向P2P平台负有还款义务的信用凭证形式，向借款人发放贷款，同时设立相对应的信用凭证。Web Bank再将这些信用证出售给放款人。这个模式有效地将借款人和放款人直接联系起?来。&nbsp;</div><div>在SEC登记注册之后，P2P平台改变了这一模式，以反映出售给放款人的信用凭证属于证券类别，同时遵守相关的证券法律。借款人的贷款仍然是由Web Bank来发放，然后Web Bank会将债权卖给P2P平台，P2P再将这些贷款又以收益权凭证形式卖给放款人。&nbsp;</div><div>通过这种形式，放款人就成了P2P平台的无担保债权人，而非是借款人的债权人，在借款人违约的情况下，放款人只有很有限的途径去追索。&nbsp;</div><div>Web Bank在整个过程中的作用就是分销贷款，这是P2P平台没有相关牌照所不能做的业务。进一步来说，考虑到有大量的法律和监管法规针对消费者贷款发行这一环节，因而委托有牌照的银行来分销贷款，有助于P2P平台符合这些法律法规的要求，也有利于美国的多头监管，从而保护借贷双方，防止欺诈。&nbsp;</div><div>可以看出，由于中美之间金融市场化程度不同和政策环境不同，因此同样是P2P，其实中美之间的差异很大。但国内存在诸多不利因素，诸如金融市场化程度很低，垄断横行，社会征信体系极不完善，政策不明朗等，却反而带来更大的发展潜力。&nbsp;</div><div>因为P2P平台面对的服务群体更多，市场容量更大，且规则尚不完善，有非常巨大的成长空间，而国内P2P也很有可能成为现有金融体系内的服务补充，这对推动整个金融行业市场化有非常深远的意义。&nbsp;</div>','admin','1382346361','362','','','0','0','0','0','0');");
E_D("replace into `ycd_article` values('41','P2P行业首发自律公约','P2P行业首发自律公约','P2P行业首发自律公约','<p>&nbsp;</p><div>&nbsp; &nbsp; 晨报讯(记者 姜樊)近几年，本身作为先进模式引进的P2P模式(个人对个人)，却往往被看作是非法集资、卷款跑路的代名词。日前，中国小额信贷联盟在京正式发布《个人对个人(P2P)小额信贷信息咨询服务机构行业自律公约》。</div><div>&nbsp; &nbsp; P2P小额信贷信息咨询服务业务作为近年来从国外引进的小额信贷创新业务，其基本原理是通过信息和互联网技术，为个人与个人之间的小额信用借贷行为提供信息咨询性质的中介服务，从而满足个人的信贷和投资需求。但是，在快速发展的背后，也出现了一些包括涉嫌非法集资、编造虚假债权、交易成本增加等诸多问题。而P2P行业目前面临准入门槛低、法律监管尚处于“真空”状态等问题。</div><div>&nbsp; &nbsp; 据了解，自律公约要求P2P服务机构必须合法合规经营，完善企业治理和内部控制机制，提升风险管理能力，针对信用风险、操作风险、流动性风险、市场风险等制定积极可行的防范措施。同时积极与征信机构合作，把借款还款信息主动纳入统一的征信体系，建立涵盖P2P服务机构借款人信息的征信管理系统平台，通过数据报送和数据查询的双向数据交流，增强P2P服务机构防范信贷风险的能力。</div>','admin','1382346400','362','','','0','0','0','0','0');");
E_D("replace into `ycd_article` values('42','P2P借贷“虚火上升” 概念误区引争议','P2P借贷“虚火上升” 概念误区引争议','P2P借贷“虚火上升” 概念误区引争议','<div>近日的互联网大会“火爆”的争论场面，是互联网金融火热程度的最好注解，央行副行长刘士余对互联网金融发展的鼓励表态也为互联网金融再增温，不过，他也对P2P行业的发展风险提出了警示。</div><div>&nbsp;</div><div>这引发了行业对P2P借贷的新一轮热议，尤其是行业线上线下模式的性质界定。有行业人士认为，近两年P2P借贷发展太热了，呈现出虚火上升的趋势，并且诸多概念存在误区。</div><div>&nbsp;</div><div>“泥沙俱下”、“良莠不齐”被认为是目前最能够形容P2P借贷行业现状的词汇。无行业门槛，给P2P借贷行业带来风险，通过互联网的虚拟化，则将这种风险放大。例如，部分P2P机构通过虚假借款人等方式募集资金，甚至一些企业竟将资金用于炒期货，这无疑为行业的健康发展埋下巨大隐患。</div><div>&nbsp;</div><div>一边是P2P借贷行业的“野蛮生长”，一边则是行业的创新模式带来争议不断。</div><div>&nbsp;</div><div>“值得欣慰的是，监管层目前注意到了行业存在的问题，对行业的调研摸底正在进行。”某P2P企业负责人表示，监管层的摸底将有利于行业将恶意存在问题的发展模式驱逐出去。</div>','admin','1382346434','362','','','0','0','0','0','0');");
E_D("replace into `ycd_article` values('43','P2P行业健康发展关键在于风险控制','P2P行业健康发展关键在于风险控制','P2P行业健康发展关键在于风险控制','<p>&nbsp;</p><div>信而富 王征宇</div><div>&nbsp;</div><div>在中央积极鼓励民间资本进入金融服务业的大背景下，有关互联网金融的讨论与实践异常火爆，而最近国家各监管机构组成互联网金融发展与监管研究小组，就行业发展、企业诉求和监管建议展开调研，更进一步激发了行业内对于互联网金融立法的猜想。而首当其冲的无疑是P2P个人对个人贷款平台。</div><div>&nbsp;</div><div>国内P2P解决服务覆盖</div><div>&nbsp;</div><div>P2P平台在国外解决的是交易效率问题，通过网络技术和征信数据，省略掉银行的中间环节，借贷双方直接交易，借款人可以支付较低的利息，而出借人可以获得较高的收益，是一种技术推动的新业务模式。在中国情况则不同，国内P2P机构的服务对象大多是未被银行等正规金融机构所覆盖到的小微企业、店主、城镇低收入居民和农民工创业者等人群。也就是说，目前国内P2P平台解决的主要是服务覆盖问题，而不是交易效率问题。</div><div>&nbsp;</div><div>目前几乎所有P2P机构都试图把自己定位为服务平台，但实际操作中，却往往把握不定所谓服务平台的操作边界。争议较大的是承诺收益或者说是保本保息问题，以及所谓平台担保问题。P2P平台实际上没有能力也不应该承诺出借资金的安全，就如同婚介所无法对婚姻破裂负责一样，如果平台承诺本金安全或者收益水平，这实际上是给出借人写了一张无法兑现的支票，因此法律的红线在这里划定了界限。这一点，必须明白无误地告知出借人。</div><div>&nbsp;</div><div>平台的基本定位，在于撮合交易，而不参与交易，交易只能是借贷双方的责任。不少人提出，P2P需要担保，而担保公司凭什么对于P2P平台上的借款行为担保？担保公司自身的杠杆是十倍，可以以其资本担保十倍的借款，如果风险损失超过这个标准怎么办？于是担保公司要求反担保，但借款人如果能够提供反担保，他完全可以与出借人形成抵押借款协议。</div><div>&nbsp;</div><div>平台作用的另外一个界限是资金流：出借人和借款人之间的资金来往，不仅应该与公司账户资金严格隔离，而且应该通过第三方托管，平台上走的是信息流，不能是资金流。</div><div>&nbsp;</div><div>线上与线下模式之争</div><div>&nbsp;</div><div>在国外，所有的P2P平台都是线上网络模式。无论是英国的Zopa，美国的Prosper和Lending Club等，这些国外大型P2P平台的交易双方均是直接通过互联网平台而实现借贷关系并完成相关交易的。互联网的介入完全颠覆了银行客户的所谓二八理论，充分体现了长尾理论的优势，无限放大了资金供求双方的范围和数量，并使借贷双方的匹配过程更加有效。</div><div>&nbsp;</div><div>但是这么做需要有两个前提：第一是强大的技术体系支持；第二是网络信息对称，个人信用体系完善，公开透明。而在中国，正是这两点制约了P2P线上平台的扩张。第一，急需金融服务但被排除在银行覆盖之外的边远地区、弱势群体，往往也被互联网排除在外，他们不拥有电脑和智能手机，对于无法接触到互联网的这部分人群该如何服务？第二，我国个人征信体系不对P2P机构开放，线上平台即使有借款人的直接授权，仍然无法获得征信信息，只能通过线下方式让借款人走到央行分支机构排队打印，这降低了线上模式的效率优势。而从风险控制的角度看，面见借款人，当面核实申请资料的真伪，仍然是十分有效的手段。</div><div>&nbsp;</div><div>因此，除了大数据拥有者，国内大部分P2P机构都多多少少往线下发展，这是不得已而为之。目前线下P2P的规模都远大于线上的事实，也佐证了这一点。线上和线下模式，从根本来说，在于渠道不同，其本质应该是一样的，就是借贷双方直接协议。渠道效率高低、是否拥有大数据，都不应该成为合理性或合法性的依据。</div><div>&nbsp;</div><div>风险控制是重中之重</div><div>&nbsp;</div><div>风险控制是P2P行业健康发展的关键，是实现民间借贷阳光化、规范化发展的关键，也是使普惠金融得以实现并可持续发展的关键。风险控制的目标，是保持风险水平在可接受的指标范围内，使得扣除风险因素后的业务收益最大化。</div><div>&nbsp;</div><div>目前关于P2P行业的风险控制问题，很多人担心的是“击鼓传花”，其解决之道，一是风险控制，二是透明公开。事实上，人们对于P2P行业的诸多担心，最终都可以通过这两个方面的要求来解决。</div><div>&nbsp;</div><div>风险控制不仅仅是逾期率，因为脱离了指标定义，数值本身毫无意义。透明的风险指标是风险控制的重点，行业的风险指标需要标准化，且必须以严肃的银行帐户现金交易记录审计为基础，这样，逾期率的计算数值才具备参考价值。中国小额信贷联盟和P2P行业委员会确定的P2P行业自律公约中，对于风险控制、风险指标进行了详细规定，这应该是一步扎扎实实的努力。</div><div>&nbsp;</div><div>为了发展业务，有不少P2P机构选择了风险拨备的方式，即在自身收入中提取一部分资金形成风险储备金，以弥补可能出现的坏账风险，从而使出借人的本息不受借款人逾期的影响。这里的关键是：实际风险水平和实际风险拨备水平的公开透明。如果没有公开透明的披露，出借人就会根据早期出借人“没有本息损失”的现象而认为平台实际上承诺了本息安全。即使在有严密监管的环境下，拨备不足也是一个严肃的问题，何况没有监管的P2P行业，因此没有公开透明的风险拨备，实际上让出借人完全处于盲人骑瞎马的状态中。</div><div>&nbsp;</div><div>P2P行业作为普惠金融的一种努力，试图解决部分不被目前金融体系覆盖的人群的资金需求问题，其社会意义和市场需求显而易见。P2P目前一方面处于快速发展过程中，一方面又处于婴儿学步的早期，过度监管和没有监管都不利于行业发展，在充分讨论的基础上，弄明白P2P行业发展面临的问题及其解决之道，是当务之急。</div>','admin','1382346479','362','','','0','0','0','0','0');");
E_D("replace into `ycd_article` values('44','P2P理财PK银行理财 谁更胜出？','P2P理财PK银行理财 谁更胜出？','P2P理财PK银行理财 谁更胜出？','<p>银率网统计数据显示，以上海市正在发售的银行理财产品为例，1个月至3个月、3个月至6个月、6个月至1年期限的银行理财产品平均预期最高年化收益分别是4.37%、4.64%和4.80%。与之相比，以业内著名P2P企业产品为例，同样期限的P2P理财产品预期年化收益分别高达8% 、9%、10.5%。同样以10万元购买一年封闭期的银行理财产品和P2P产品，前者的收益尚不及后者的一半。</p>','admin','1382346506','362','','','0','0','0','0','0');");
E_D("replace into `ycd_article` values('50','如何防范钓鱼网','如何防范钓鱼网','如何防范钓鱼网','<p>&nbsp;</p><div>&nbsp; &nbsp; 众所周知，网购虽然方便、快捷，但它不像线下购物可以当面一手交钱一手交货，消费者网上购物大多只有付了钱才可以收到商品，而且在网上交易的过程中还存在一些安全隐患，比如“网络钓鱼”。“网络钓鱼”是一种新型的互联网诈骗犯罪行为，骗子通常会通过QQ、MSN或者SNS等渠道发送一些“高仿真模仿”淘宝、拍拍等知名购物网站的虚假页面链接，使消费者误以为是在正规的网站进行购物，继而通过电子支付渠道将自己的钱款汇入骗子的银行账户。</div><div>&nbsp; &nbsp;网购时注意事项：第一、网购过程中一定要提高防范意识，一旦遇到需要输入账号、密码等环节，一定要仔细核实网址是否准确无误，再进行填写。更不要轻易尝试点击QQ、E-mail等方式得到的交易链接；第二、不在对方以低价或其他理由发送的站外商品页面、付款页面等处交易，采用第三方支付平台如支付宝、财付通、易宝支付等进行支付，可最大限度保障交易资金的安全；第三、为经常网购的电脑安装杀毒软件，并设定为自动更新，确保自己的账户与资金安全免受病毒、木马等非钓鱼类威胁；</div>','admin','1382685776','390','','','0','0','0','0','0');");
E_D("replace into `ycd_article` values('51','如何安全上网','如何安全上网','如何安全上网','<p>&nbsp;</p><div>1.安全补丁更新：</div><div>&nbsp; 一些常用的计算机软件的保安漏洞可被病毒作者和黑客利用，来进入那些未安装安全补丁程序的计算机，盗取资料。一旦发现这种情况，软件出版商便会推出安全补丁程序来堵塞这些漏洞。您可定期浏览软件出版商的网站，对操作系统和应用软件进行安全补丁更新。</div><div>&nbsp; 2.安装防病毒软件：</div><div>&nbsp; 安装防病毒软件，定期更新软件及安装最新的病毒定义文件，有效保障计算机免受病毒侵袭。</div><div>&nbsp; 3.安装个人防火墙：</div><div>&nbsp; 安装防火墙，帮助保护您的计算机系统不会在连接互联网时受到侵袭，可阻止资料在未经您授权下传入或传出您的计算机。</div><div>&nbsp; 4.养成良好的使用习惯。</div><div>&nbsp; 下载软件到官方网页，或者到自己熟悉的网站下载，不要浏览不良网站，不要随意打开陌生人的邮箱、网址链接等。</div>','admin','1382685814','390','','','0','0','0','0','0');");

